import React, { useState } from 'react';
import heroImage from './hero-image.jpg'; // Replace with your image path

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      title: "GET ARTICLE PUBLISHED IN JUST 48 HOURS!",
      subtitle: "DIGITAL PRESS RELEASE",
      description: "Don't Miss the Opportunity to Share Your Story.",
      buttonText: "BOOK NOW"
    },
    {
      title: "Hire Dedicated Media Planner",
      subtitle: "For your business Today!",
      description: "Just â‚¹99/-",
      buttonText: "Learn More"
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <div style={styles.container}>
      {/* Navigation Links */}
      <div style={styles.navLinks}>
        <a href="#newspaper" style={styles.navLink}>Newspaper Advertising</a>
        <a href="#digital" style={styles.navLink}>Digital Marketing</a>
        <a href="#radio" style={styles.navLink}>Radio Advertising</a>
        <a href="#cinema" style={styles.navLink}>Cinema Advertising</a>
        <a href="#digital-pr" style={styles.navLink}>Digital PR</a>
      </div>

      {/* Hero Image Slider */}
      <div style={styles.heroContainer}>
        <img 
          src={heroImage} 
          alt="Advertising" 
          style={styles.heroImage}
        />
        <div style={styles.slideContent}>
          <h2 style={styles.slideTitle}>{slides[currentSlide].title}</h2>
          <h3 style={styles.slideSubtitle}>{slides[currentSlide].subtitle}</h3>
          <p style={styles.slideDescription}>{slides[currentSlide].description}</p>
          <button style={styles.slideButton}>
            {slides[currentSlide].buttonText}
          </button>
        </div>
        <button style={styles.prevButton} onClick={prevSlide}>â€¹</button>
        <button style={styles.nextButton} onClick={nextSlide}>â€º</button>
      </div>

      {/* Deals Section */}
      <div style={styles.dealsContainer}>
        <h3 style={styles.dealsTitle}>Deals and Offers</h3>
        <div style={styles.dealsGrid}>
          <div style={styles.dealCard}>
            <p style={styles.dealText}>Let the whole city hear about your brand!</p>
            <button style={styles.dealButton}>Book Now</button>
            <p style={styles.dealSubtext}>Create a Family at Last Place</p>
          </div>
          <div style={styles.dealCard}>
            <p style={styles.dealText}>USE CODE | WINTER50</p>
            <div style={styles.newsGrid}>
              <span style={styles.newsItem}>ZEENEWS</span>
              <span style={styles.newsItem}>mid-day</span>
              <span style={styles.newsItem}>oneindia</span>
              <span style={styles.newsItem}>MME NEW</span>
              <span style={styles.newsItem}>ANI</span>
              <span style={styles.newsItem}>A dbp</span>
              <span style={styles.newsItem}>/CAN-20</span>
              <span style={styles.newsItem}>SIR HER</span>
              <span style={styles.newsItem}>FILMBEAT</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Arial', sans-serif",
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
  },
  navLinks: {
    display: 'flex',
    justifyContent: 'center',
    gap: '30px',
    marginBottom: '20px',
    flexWrap: 'wrap',
  },
  navLink: {
    color: '#333',
    textDecoration: 'none',
    fontWeight: '600',
    fontSize: '16px',
    padding: '10px 0',
    position: 'relative',
  },
  heroContainer: {
    position: 'relative',
    height: '500px',
    marginBottom: '40px',
    borderRadius: '10px',
    overflow: 'hidden',
  },
  heroImage: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  slideContent: {
    position: 'absolute',
    bottom: '50px',
    left: '50px',
    maxWidth: '500px',
    backgroundColor: 'rgba(255,255,255,0.9)',
    padding: '30px',
    borderRadius: '10px',
  },
  slideTitle: {
    fontSize: '28px',
    fontWeight: '700',
    marginBottom: '10px',
    color: '#333',
  },
  slideSubtitle: {
    fontSize: '20px',
    fontWeight: '600',
    marginBottom: '15px',
    color: '#ff6b00',
  },
  slideDescription: {
    fontSize: '16px',
    marginBottom: '20px',
    color: '#555',
  },
  slideButton: {
    backgroundColor: '#ff6b00',
    color: 'white',
    border: 'none',
    padding: '12px 30px',
    borderRadius: '5px',
    fontWeight: '600',
    cursor: 'pointer',
  },
  prevButton: {
    position: 'absolute',
    left: '20px',
    top: '50%',
    transform: 'translateY(-50%)',
    backgroundColor: 'rgba(0,0,0,0.5)',
    color: 'white',
    border: 'none',
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    fontSize: '20px',
    cursor: 'pointer',
  },
  nextButton: {
    position: 'absolute',
    right: '20px',
    top: '50%',
    transform: 'translateY(-50%)',
    backgroundColor: 'rgba(0,0,0,0.5)',
    color: 'white',
    border: 'none',
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    fontSize: '20px',
    cursor: 'pointer',
  },
  dealsContainer: {
    marginTop: '40px',
  },
  dealsTitle: {
    fontSize: '24px',
    fontWeight: '700',
    marginBottom: '20px',
    textAlign: 'center',
  },
  dealsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
    gap: '30px',
  },
  dealCard: {
    backgroundColor: '#f8f9fa',
    padding: '30px',
    borderRadius: '10px',
    boxShadow: '0 5px 15px rgba(0,0,0,0.1)',
  },
  dealText: {
    fontSize: '18px',
    fontWeight: '600',
    marginBottom: '15px',
  },
  dealButton: {
    backgroundColor: '#ff6b00',
    color: 'white',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    fontWeight: '600',
    cursor: 'pointer',
    marginBottom: '15px',
  },
  dealSubtext: {
    fontSize: '14px',
    color: '#666',
    fontStyle: 'italic',
  },
  newsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(3, 1fr)',
    gap: '10px',
  },
  newsItem: {
    padding: '8px',
    textAlign: 'center',
    backgroundColor: '#eee',
    borderRadius: '5px',
    fontSize: '14px',
  },
  '@media (max-width: 768px)': {
    navLinks: {
      gap: '15px',
    },
    heroContainer: {
      height: '400px',
    },
    slideContent: {
      left: '20px',
      bottom: '20px',
      padding: '20px',
      maxWidth: '80%',
    },
    slideTitle: {
      fontSize: '22px',
    },
    slideSubtitle: {
      fontSize: '18px',
    },
  },
  '@media (max-width: 480px)': {
    navLinks: {
      gap: '10px',
      fontSize: '14px',
    },
    heroContainer: {
      height: '300px',
    },
    slideContent: {
      padding: '15px',
    },
    dealsGrid: {
      gridTemplateColumns: '1fr',
    },
  },
};

export default Hero;
