import React, { useState } from 'react';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  
  const slides = [
    {
      title: "Easiest and Fastest ad booking",
      subtitle: "at the Lowest Price",
      description: "Book your Newspaper display ads in any regional or National Newspaper, Instantly!",
      buttonText: "BOOK NOW"
    }
  ];

  const handleBookNow = () => {
    alert('Booking functionality would be implemented here');
  };

  return (
    <section style={styles.hero}>
      <div style={styles.heroContent}>
        <div style={styles.heroText}>
          <h1 style={styles.heroTitle}>
            {slides[currentSlide].title}<br />
            <span style={styles.heroSubtitle}>{slides[currentSlide].subtitle}</span>
          </h1>
          <p style={styles.heroDescription}>{slides[currentSlide].description}</p>
          <button 
            style={styles.heroButton}
            onClick={handleBookNow}
          >
            {slides[currentSlide].buttonText}
          </button>
        </div>
      </div>
    </section>
  );
};

const styles = {
  hero: {
    backgroundColor: '#f5f5f5',
    padding: '80px 20px',
    textAlign: 'center',
    backgroundImage: 'linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5))',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    color: '#fff',
  },
  heroContent: {
    maxWidth: '800px',
    margin: '0 auto',
  },
  heroText: {
    padding: '20px',
  },
  heroTitle: {
    fontSize: '2.5rem',
    marginBottom: '20px',
    lineHeight: '1.3',
    fontWeight: '700',
  },
  heroSubtitle: {
    color: '#ff6b00',
    display: 'inline-block',
  },
  heroDescription: {
    fontSize: '1.2rem',
    marginBottom: '30px',
    lineHeight: '1.6',
  },
  heroButton: {
    backgroundColor: '#ff6b00',
    color: '#fff',
    border: 'none',
    padding: '12px 30px',
    fontSize: '1rem',
    borderRadius: '5px',
    cursor: 'pointer',
    fontWeight: '600',
    transition: 'background-color 0.3s',
  },
  
  // Media queries for responsive design
  '@media (max-width: 768px)': {
    hero: {
      padding: '60px 20px',
    },
    heroTitle: {
      fontSize: '2rem',
    },
    heroDescription: {
      fontSize: '1rem',
    },
  },
  
  '@media (max-width: 480px)': {
    heroTitle: {
      fontSize: '1.8rem',
    },
  },
};

export default Hero;
