import React, { useState, useEffect } from "react";

const Hero = () => {
  const slides = [
    {
      title: "Easiest and Fastest ad booking",
      subtitle: "at the Lowest Price",
      description: "Book your Newspaper display ads in any regional or National Newspaper, Instantly!",
      image: "https://images.unsplash.com/photo-1504711434969-e33886168f5c?w=400&h=280&fit=crop&auto=format",
      button: "BOOK NOW"
    },
    {
      title: "Digital Marketing Solutions", 
      subtitle: "at Affordable Rates",
      description: "Reach millions with our comprehensive digital marketing services!",
      image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?w=400&h=280&fit=crop&auto=format",
      button: "BOOK NOW"
    },
    {
      title: "Radio & Cinema Advertising",
      subtitle: "Maximum Reach & Impact",
      description: "Connect with your audience through powerful audio-visual campaigns!",
      image: "https://images.unsplash.com/photo-1478720568477-152d9b164e26?w=400&h=280&fit=crop&auto=format",
      button: "BOOK NOW"
    }
  ];

  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 6000);
    return () => clearInterval(timer);
  }, []);

  const goPrev = () => setCurrent((prev) => (prev - 1 + slides.length) % slides.length);
  const goNext = () => setCurrent((prev) => (prev + 1) % slides.length);

  return (
    <div style={{ 
      position: "relative",
      background: "linear-gradient(135deg, #4472C4 0%, #5B7BD6 50%, #6B8DE8 100%)",
      fontFamily: "Arial, sans-serif",
      minHeight: "500px",
      padding: "50px 0",
      overflow: "hidden"
    }}>
      {/* Animated Background Elements */}
      <div style={{
        position: "absolute",
        top: "20%",
        left: "10%",
        width: "100px",
        height: "100px",
        background: "rgba(255,255,255,0.05)",
        borderRadius: "50%",
        animation: "float 6s ease-in-out infinite"
      }}></div>
      <div style={{
        position: "absolute",
        top: "60%",
        right: "15%",
        width: "60px",
        height: "60px",
        background: "rgba(255,255,255,0.03)",
        borderRadius: "50%",
        animation: "float 4s ease-in-out infinite reverse"
      }}></div>

      <style>
        {`
          @keyframes float {
            0%, 100% { transform: translateY(0px); }
            50% { transform: translateY(-20px); }
          }
          
          @keyframes slideIn {
            from { opacity: 0; transform: translateX(30px); }
            to { opacity: 1; transform: translateX(0); }
          }
          
          .slide-content {
            animation: slideIn 0.8s ease-out;
          }
        `}
      </style>

      {slides.map((slide, index) => (
        <div
          key={index}
          style={{
            display: index === current ? "block" : "none",
            opacity: index === current ? 1 : 0,
            transition: "opacity 1s ease-in-out"
          }}
        >
          <div style={{
            maxWidth: "1200px",
            margin: "0 auto",
            padding: "50px 20px",
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: "60px",
            alignItems: "center",
            minHeight: "400px"
          }}>
            {/* Left Image */}
            <div style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
              <div style={{
                backgroundColor: "rgba(255,255,255,0.15)",
                borderRadius: "25px",
                padding: "30px",
                boxShadow: "0 25px 60px rgba(0,0,0,0.3), inset 0 1px 0 rgba(255,255,255,0.2)",
                width: "100%",
                maxWidth: "480px",
                backdrop: "blur(10px)",
                border: "1px solid rgba(255,255,255,0.1)"
              }}>
                <img 
                  src={slide.image}
                  alt="Business advertising"
                  style={{
                    width: "100%",
                    height: "340px",
                    objectFit: "cover",
                    borderRadius: "20px",
                    boxShadow: "0 15px 35px rgba(0,0,0,0.2)"
                  }}
                />
              </div>
            </div>

            {/* Right Content */}
            <div style={{ color: "white", textAlign: "left" }} className="slide-content">
              <div style={{
                background: "rgba(255,255,255,0.05)",
                padding: "8px 16px",
                borderRadius: "20px",
                display: "inline-block",
                marginBottom: "20px",
                border: "1px solid rgba(255,255,255,0.1)",
                fontSize: "12px",
                fontWeight: "bold",
                textTransform: "uppercase",
                letterSpacing: "1px"
              }}>
                ✨ Premium Service
              </div>

              <h1 style={{
                fontSize: "48px",
                fontWeight: "bold",
                lineHeight: "1.1",
                margin: "0 0 5px 0",
                textShadow: "2px 2px 4px rgba(0,0,0,0.3)",
                color: "white",
                background: "linear-gradient(45deg, #fff, #f0f8ff)",
                WebkitBackgroundClip: "text",
                WebkitTextFillColor: "transparent",
                backgroundClip: "text"
              }}>
                {slide.title}
              </h1>
              <h2 style={{
                fontSize: "48px", 
                fontWeight: "bold",
                lineHeight: "1.1",
                margin: "0 0 30px 0",
                textShadow: "2px 2px 4px rgba(0,0,0,0.3)",
                color: "#FFE135",
                textTransform: "uppercase"
              }}>
                {slide.subtitle}
              </h2>
              
              <p style={{
                fontSize: "22px",
                lineHeight: "1.6",
                margin: "0 0 40px 0",
                opacity: "0.95",
                color: "white",
                maxWidth: "520px",
                textShadow: "1px 1px 2px rgba(0,0,0,0.2)"
              }}>
                {slide.description}
              </p>

              <div style={{ display: "flex", gap: "15px", alignItems: "center" }}>
                <button style={{
                  background: "linear-gradient(45deg, #E53E3E, #ff4757)",
                  color: "white",
                  padding: "16px 32px",
                  fontSize: "16px",
                  fontWeight: "bold",
                  border: "none",
                  borderRadius: "30px",
                  cursor: "pointer",
                  boxShadow: "0 8px 25px rgba(229, 62, 62, 0.4), inset 0 1px 0 rgba(255,255,255,0.2)",
                  transition: "all 0.3s ease",
                  textTransform: "uppercase",
                  letterSpacing: "1px"
                }}
                onMouseOver={(e) => {
                  e.target.style.transform = "translateY(-2px)";
                  e.target.style.boxShadow = "0 12px 35px rgba(229, 62, 62, 0.5)";
                }}
                onMouseOut={(e) => {
                  e.target.style.transform = "translateY(0)";
                  e.target.style.boxShadow = "0 8px 25px rgba(229, 62, 62, 0.4)";
                }}>
                  {slide.button} →
                </button>

                <div style={{
                  color: "rgba(255,255,255,0.8)",
                  fontSize: "14px",
                  fontWeight: "500"
                }}>
                  🚀 Instant Booking Available
                </div>
              </div>
            </div>
          </div>
        </div>
      ))}

      {/* Enhanced Navigation Arrows */}
      <button onClick={goPrev} style={{
        position: "absolute",
        left: "30px",
        top: "50%",
        transform: "translateY(-50%)",
        background: "rgba(255,255,255,0.95)",
        border: "none",
        borderRadius: "50%",
        width: "50px",
        height: "50px",
        cursor: "pointer",
        fontSize: "18px",
        color: "#4472C4",
        boxShadow: "0 5px 20px rgba(0,0,0,0.3)",
        transition: "all 0.3s ease"
      }}
      onMouseOver={(e) => {
        e.target.style.transform = "translateY(-50%) scale(1.1)";
        e.target.style.boxShadow = "0 8px 30px rgba(0,0,0,0.4)";
      }}
      onMouseOut={(e) => {
        e.target.style.transform = "translateY(-50%) scale(1)";
        e.target.style.boxShadow = "0 5px 20px rgba(0,0,0,0.3)";
      }}>❮</button>
      
      <button onClick={goNext} style={{
        position: "absolute", 
        right: "30px",
        top: "50%",
        transform: "translateY(-50%)",
        background: "rgba(255,255,255,0.95)",
        border: "none",
        borderRadius: "50%",
        width: "50px",
        height: "50px", 
        cursor: "pointer",
        fontSize: "18px",
        color: "#4472C4",
        boxShadow: "0 5px 20px rgba(0,0,0,0.3)",
        transition: "all 0.3s ease"
      }}
      onMouseOver={(e) => {
        e.target.style.transform = "translateY(-50%) scale(1.1)";
        e.target.style.boxShadow = "0 8px 30px rgba(0,0,0,0.4)";
      }}
      onMouseOut={(e) => {
        e.target.style.transform = "translateY(-50%) scale(1)";
        e.target.style.boxShadow = "0 5px 20px rgba(0,0,0,0.3)";
      }}>❯</button>

      {/* Enhanced Slide Indicators */}
      <div style={{
        position: "absolute",
        bottom: "25px",
        left: "50%",
        transform: "translateX(-50%)",
        display: "flex",
        gap: "12px",
        background: "rgba(255,255,255,0.1)",
        padding: "10px 20px",
        borderRadius: "25px",
        backdropFilter: "blur(10px)"
      }}>
        {slides.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrent(index)}
            style={{
              width: index === current ? "30px" : "12px",
              height: "12px", 
              borderRadius: "6px",
              border: "none",
              backgroundColor: index === current ? "white" : "rgba(255,255,255,0.4)",
              cursor: "pointer",
              transition: "all 0.3s ease"
            }}
          />
        ))}
      </div>

      {/* Enhanced Fixed Contact Icons */}
      <div style={{
        position: "fixed",
        left: "0",
        top: "50%",
        transform: "translateY(-50%)",
        zIndex: 1000,
        display: "flex",
        flexDirection: "column",
        gap: "5px"
      }}>
        <div style={{
          backgroundColor: "#E53E3E",
          color: "white",
          padding: "15px",
          borderRadius: "0 12px 12px 0",
          cursor: "pointer",
          fontSize: "18px",
          boxShadow: "0 5px 20px rgba(229, 62, 62, 0.4)",
          transition: "all 0.3s ease"
        }}
        onMouseOver={(e) => {
          e.target.style.transform = "translateX(5px)";
          e.target.style.backgroundColor = "#cc3333";
        }}
        onMouseOut={(e) => {
          e.target.style.transform = "translateX(0)";
          e.target.style.backgroundColor = "#E53E3E";
        }}>📞</div>
        <div style={{
          backgroundColor: "#25D366",
          color: "white", 
          padding: "15px",
          borderRadius: "0 12px 12px 0",
          cursor: "pointer",
          fontSize: "18px",
          boxShadow: "0 5px 20px rgba(37, 211, 102, 0.4)",
          transition: "all 0.3s ease"
        }}
        onMouseOver={(e) => {
          e.target.style.transform = "translateX(5px)";
          e.target.style.backgroundColor = "#1ea952";
        }}
        onMouseOut={(e) => {
          e.target.style.transform = "translateX(0)";
          e.target.style.backgroundColor = "#25D366";
        }}>💬</div>
      </div>
    </div>
  );
};

export default Hero;
