import React, { useState, useEffect } from "react";

const Hero = () => {
  const slides = [
    {
      title: "Easiest and Fastest ad booking",
      subtitle: "at the Lowest Price",
      description: "Book your Newspaper display ads in any regional or National Newspaper, Instantly!",
      image: "/api/placeholder/400/280",
      button: "BOOK NOW"
    },
    {
      title: "Digital Marketing Solutions", 
      subtitle: "at Affordable Rates",
      description: "Reach millions with our comprehensive digital marketing services!",
      image: "/api/placeholder/400/280",
      button: "BOOK NOW"
    }
  ];

  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  const goPrev = () => setCurrent((prev) => (prev - 1 + slides.length) % slides.length);
  const goNext = () => setCurrent((prev) => (prev + 1) % slides.length);

  return (
    <div style={{ 
      position: "relative",
      background: "linear-gradient(135deg, #4472C4 0%, #5B7BD6 100%)",
      fontFamily: "Arial, sans-serif",
      minHeight: "320px"
    }}>
      {slides.map((slide, index) => (
        <div
          key={index}
          style={{
            display: index === current ? "block" : "none",
            transition: "opacity 0.8s ease"
          }}
        >
          <div style={{
            maxWidth: "1200px",
            margin: "0 auto",
            padding: "30px 20px",
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gap: "40px",
            alignItems: "center",
            minHeight: "280px"
          }}>
            {/* Left Content */}
            <div style={{ color: "white" }}>
              <h1 style={{
                fontSize: "36px",
                fontWeight: "bold",
                lineHeight: "1.2",
                margin: "0 0 8px 0",
                textShadow: "1px 1px 2px rgba(0,0,0,0.2)"
              }}>
                {slide.title}
              </h1>
              <h2 style={{
                fontSize: "36px", 
                fontWeight: "bold",
                lineHeight: "1.2",
                margin: "0 0 20px 0",
                textShadow: "1px 1px 2px rgba(0,0,0,0.2)"
              }}>
                {slide.subtitle}
              </h2>
              
              <p style={{
                fontSize: "16px",
                lineHeight: "1.4",
                margin: "0 0 25px 0",
                opacity: "0.95"
              }}>
                {slide.description}
              </p>

              <button style={{
                backgroundColor: "#333333",
                color: "white",
                padding: "12px 24px",
                fontSize: "14px",
                fontWeight: "bold",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
                boxShadow: "0 3px 8px rgba(0,0,0,0.3)",
                transition: "all 0.3s ease"
              }}
              onMouseOver={(e) => e.target.style.backgroundColor = "#222"}
              onMouseOut={(e) => e.target.style.backgroundColor = "#333"}>
                {slide.button}
              </button>
            </div>

            {/* Right Image */}
            <div style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}>
              <div style={{
                backgroundColor: "white",
                borderRadius: "12px",
                padding: "15px",
                boxShadow: "0 8px 25px rgba(0,0,0,0.2)",
                width: "100%",
                maxWidth: "380px"
              }}>
                <img 
                  src={slide.image}
                  alt="Business person reading newspaper"
                  style={{
                    width: "100%",
                    height: "250px",
                    objectFit: "cover",
                    borderRadius: "8px"
                  }}
                />
              </div>
            </div>
          </div>
        </div>
      ))}

      {/* Navigation Arrows */}
      <button onClick={goPrev} style={{
        position: "absolute",
        left: "15px",
        top: "50%",
        transform: "translateY(-50%)",
        background: "rgba(255,255,255,0.9)",
        border: "none",
        borderRadius: "50%",
        width: "40px",
        height: "40px",
        cursor: "pointer",
        fontSize: "16px",
        color: "#4472C4",
        boxShadow: "0 2px 8px rgba(0,0,0,0.2)"
      }}>❮</button>
      
      <button onClick={goNext} style={{
        position: "absolute", 
        right: "15px",
        top: "50%",
        transform: "translateY(-50%)",
        background: "rgba(255,255,255,0.9)",
        border: "none",
        borderRadius: "50%",
        width: "40px",
        height: "40px", 
        cursor: "pointer",
        fontSize: "16px",
        color: "#4472C4",
        boxShadow: "0 2px 8px rgba(0,0,0,0.2)"
      }}>❯</button>

      {/* Slide Indicators */}
      <div style={{
        position: "absolute",
        bottom: "15px",
        left: "50%",
        transform: "translateX(-50%)",
        display: "flex",
        gap: "8px"
      }}>
        {slides.map((_, index) => (
          <button
            key={index}
            onClick={() => setCurrent(index)}
            style={{
              width: "10px",
              height: "10px", 
              borderRadius: "50%",
              border: "none",
              backgroundColor: index === current ? "white" : "rgba(255,255,255,0.5)",
              cursor: "pointer",
              transition: "all 0.3s ease"
            }}
          />
        ))}
      </div>

      {/* Fixed Contact Icons */}
      <div style={{
        position: "fixed",
        left: "0",
        top: "50%",
        transform: "translateY(-50%)",
        zIndex: 1000,
        display: "flex",
        flexDirection: "column",
        gap: "3px"
      }}>
        <div style={{
          backgroundColor: "#E53E3E",
          color: "white",
          padding: "12px",
          borderRadius: "0 6px 6px 0",
          cursor: "pointer",
          fontSize: "16px"
        }}>📞</div>
        <div style={{
          backgroundColor: "#25D366",
          color: "white", 
          padding: "12px",
          borderRadius: "0 6px 6px 0",
          cursor: "pointer",
          fontSize: "16px"
        }}>📱</div>
      </div>
    </div>
  );
};

export default Hero;
