import React, { useState, useEffect } from 'react';

const DealsOffers = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  
  const banners = [
    {
      id: 1,
      title: "Get Your Digital PR @Unbelelvable Price!",
      subtitle: "Our Price starting only 1499/-*",
      buttonText: "Book Now",
      background: "linear-gradient(135deg, #8b4513 0%, #d2b48c 100%)",
      image: "digital-pr"
    },
    {
      id: 2, 
      title: "Lights, Camera, Advertise!",
      subtitle: "Reach Millions of Eyeballs With Hyperlocal Cinema Ad! Starting at Rs. 11000/-*",
      buttonText: "Book Now",
      background: "linear-gradient(135deg, #ffeb3b 0%, #ffc107 100%)",
      image: "cinema"
    }
  ];

  const mediaServices = [
    {
      title: "Newspaper Advertising",
      icon: "📰",
      image: "/api/placeholder/280/180",
      bgColor: "#e3f2fd"
    },
    {
      title: "Digital Marketing",
      icon: "💻", 
      image: "/api/placeholder/280/180",
      bgColor: "#e3f2fd"
    },
    {
      title: "Radio Advertising",
      icon: "📻",
      image: "/api/placeholder/280/180", 
      bgColor: "#e3f2fd"
    },
    {
      title: "Cinema Advertising",
      icon: "🎬",
      image: "/api/placeholder/280/180",
      bgColor: "#e3f2fd"
    },
    {
      title: "Digital PR",
      icon: "📱",
      image: "/api/placeholder/280/180",
      bgColor: "#e3f2fd"
    },
    {
      title: "Influencer Marketing", 
      icon: "👥",
      image: "/api/placeholder/280/180",
      bgColor: "#e3f2fd"
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % banners.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div style={styles.wrapper}>
      {/* Deals and Offers Section */}
      <section style={styles.dealsSection}>
        <div style={styles.container}>
          <h2 style={styles.sectionTitle}>Deals and Offers</h2>
          
          {/* Banner Carousel */}
          <div style={styles.carouselContainer}>
            <div 
              style={{
                ...styles.carousel,
                transform: `translateX(-${currentSlide * 50}%)`
              }}
            >
              {/* First Banner - Digital PR */}
              <div style={{...styles.bannerSlide, background: banners[0].background}}>
                <div style={styles.bannerContent}>
                  <div style={styles.bannerLeft}>
                    <h3 style={styles.bannerTitle}>{banners[0].title}</h3>
                    <p style={styles.bannerSubtitle}>{banners[0].subtitle}</p>
                    <button style={styles.bannerButton}>{banners[0].buttonText}</button>
                  </div>
                  <div style={styles.bannerRight}>
                    <div style={styles.digitalPrGraphic}>
                      <div style={styles.laptopIcon}>💻</div>
                      <div style={styles.chartElements}>
                        <div style={styles.chartBar}></div>
                        <div style={styles.chartBar}></div>
                        <div style={styles.chartBar}></div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Second Banner - Cinema */}
              <div style={{...styles.bannerSlide, background: banners[1].background}}>
                <div style={styles.bannerContent}>
                  <div style={styles.bannerLeft}>
                    <h3 style={styles.bannerTitleDark}>{banners[1].title}</h3>
                    <p style={styles.bannerSubtitleDark}>{banners[1].subtitle}</p>
                    <button style={styles.bannerButtonDark}>{banners[1].buttonText}</button>
                  </div>
                  <div style={styles.bannerRight}>
                    <div style={styles.cinemaGraphic}>
                      <div style={styles.popcornIcon}>🍿</div>
                      <div style={styles.filmIcon}>🎬</div>
                      <div style={styles.ticketIcon}>🎫</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            
            {/* Carousel Dots */}
            <div style={styles.carouselDots}>
              {banners.map((_, index) => (
                <button
                  key={index}
                  style={{
                    ...styles.dot,
                    backgroundColor: currentSlide === index ? '#ff4444' : '#ccc'
                  }}
                  onClick={() => setCurrentSlide(index)}
                />
