            </div>

            {/* Digital Marketing */}
            <div style={styles.mediaCard}>
              <div style={{...styles.mediaImageContainer, backgroundColor: "#e3f2fd"}}>
                <div style={styles.digitalIcons}>
                  <div style={styles.socialIcon}>📱</div>
                  <div style={styles.socialIcon}>💻</div>
                  <div style={styles.socialIcon}>📊</div>
                  <div style={styles.cloudShape}></div>
                </div>
              </div>
              <div style={styles.mediaCardContent}>
                <h3 style={styles.mediaCardTitle}>Start Your</h3>
                <h4 style={styles.mediaCardService}>Digital Marketing</h4>
                <button style={styles.mediaCardButton}>Book Now</button>
              </div>
            </div>

            {/* Radio Advertising */}
            <div style={styles.mediaCard}>
              <div style={{...styles.mediaImageContainer, backgroundColor: "#e3f2fd"}}>
                <div style={styles.radioGraphic}>
                  <div style={styles.radioIcon}>📻</div>
                  <div style={styles.micIcon}>🎙️</div>
                  <div style={styles.onAirText}>ON AIR</div>
                </div>
              </div>
              <div style={styles.mediaCardContent}>
                <h3 style={styles.mediaCardTitle}>Start Your</h3>
                <h4 style={styles.mediaCardService}>Radio Advertising</h4>
                <button style={styles.mediaCardButton}>Book Now</button>
              </div>
            </div>

            {/* Cinema Advertising */}
            <div style={styles.mediaCard}>
              <div style={{...styles.mediaImageContainer, backgroundColor: "#e3f2fd"}}>
                <div style={styles.cinemaIcon}>
                  <div style={styles.cinemaText}>CINEMA</div>
                  <div style={styles.advertisingText}>ADVERTISING</div>
                </div>
              </div>
              <div style={styles.mediaCardContent}>
                <h3 style={styles.mediaCardTitle}>Start Your</h3>
                <h4 style={styles.mediaCardService}>Cinema Advertising</h4>
                <button style={styles.mediaCardButton}>Book Now</button>
              </div>
            </div>

            {/* Digital PR */}
            <div style={styles.mediaCard}>
              <div style={{...styles.mediaImageContainer, backgroundColor: "#e3f2fd"}}>
                <div style={styles.digitalPrIcons}>
                  <div style={styles.newsIcon}>📰</div>
                  <div style={styles.webIcon}>🌐</div>
                  <div style={styles.prIcon}>📢</div>
                </div>
              </div>
              <div style={styles.mediaCardContent}>
                <h3 style={styles.mediaCardTitle}>Start Your</h3>
                <h4 style={styles.mediaCardService}>Digital PR</h4>
                <button style={styles.mediaCardButton}>Book Now</button>
              </div>
            </div>

            {/* Influencer Marketing */}
            <div style={styles.mediaCard}>
              <div style={{...styles.mediaImageContainer, backgroundColor: "#e3f2fd"}}>
                <div style={styles.influencerGraphic}>
                  <div style={styles.influencerIcon}>👩‍💼</div>
                  <div style={styles.heartIcon}>❤️</div>
                  <div style={styles.followersText}>👥</div>
                </div>
              </div>
              <div style={styles.mediaCardContent}>
                <h3 style={styles.mediaCardTitle}>Start Your</h3>
                <h4 style={styles.mediaCardService}>Influencer Marketing</h4>
                <button style={styles.mediaCardButton}>Book Now</button>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

