import React, { useState } from 'react';

const DealsOffers = ({ deals }) => {
  const [selectedDeal, setSelectedDeal] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Extended deals with more options
  const allDeals = [
    ...deals,
    {
      id: 3,
      title: "Social Media Marketing Package",
      description: "Complete social media management for your brand",
      price: "Starting at Rs. 2999/-*",
      category: "digital",
      features: ["Content Creation", "Post Scheduling", "Analytics", "Community Management"],
      validTill: "31st Dec 2024"
    },
    {
      id: 4,
      title: "Newspaper Ad Combo",
      description: "Book ads in 5 newspapers and get 2 free!",
      price: "Save up to 40%",
      category: "newspaper",
      features: ["Display Ads", "Classified Ads", "Multiple Cities", "Free Design"],
      validTill: "15th Dec 2024"
    }
  ];

  const handleDealClick = (deal) => {
    setSelectedDeal(deal);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedDeal(null);
  };

  const handleBookDeal = (dealTitle) => {
    alert(`Booking ${dealTitle}... Redirecting to booking form.`);
    closeModal();
  };

  const getDealIcon = (category) => {
    const iconMap = {
      'cinema': 'ðŸŽ¬',
      'media-planning': 'ðŸ“Š',
      'digital': 'ðŸ’»',
      'newspaper': 'ðŸ“°',
      'radio': 'ðŸ“»'
    };
    return iconMap[category] || 'ðŸŽ¯';
  };

  const getDealColor = (category) => {
    const colorMap = {
      'cinema': '#e74c3c',
      'media-planning': '#3498db',
      'digital': '#2ecc71',
      'newspaper': '#f39c12',
      'radio': '#9b59b6'
    };
    return colorMap[category] || '#34495e';
  };

  return (
    <section className="deals" id="deals">
      <div className="container">
        <h2>Deals and Offers</h2>
        <p style={{ 
          textAlign: 'center', 
          fontSize: '1.1rem', 
          color: '#666', 
          marginBottom: '50px' 
        }}>
          Limited time offers to boost your business at unbeatable prices!
        </p>
        
        <div className="deals-grid">
          {allDeals.map((deal) => (
            <div key={deal.id} className="deal-card">
              <div 
                className="deal-image"
                style={{ background: getDealColor(deal.category) }}
              >
                <span style={{ fontSize: '4rem' }}>
                  {getDealIcon(deal.category)}
                </span>
              </div>
              
              <div className="deal-content">
                <div style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  marginBottom: '15px'
                }}>
                  <span style={{
                    background: getDealColor(deal.category),
                    color: 'white',
                    padding: '4px 8px',
                    borderRadius: '12px',
                    fontSize: '0.8rem',
                    fontWeight: '500',
                    textTransform: 'uppercase'
                  }}>
                    {deal.category}
                  </span>
                  
                  {deal.validTill && (
                    <span style={{
                      background: '#dc3545',
                      color: 'white',
                      padding: '4px 8px',
                      borderRadius: '12px',
                      fontSize: '0.7rem',
                      fontWeight: '500'
                    }}>
                      Valid till {deal.validTill}
                    </span>
                  )}
                </div>
                
                <h3>{deal.title}</h3>
                <p>{deal.description}</p>
                
                {deal.features && (
                  <ul style={{ 
                    listStyle: 'none', 
                    padding: '0', 
                    margin: '15px 0',
                    fontSize: '0.9rem',
                    color: '#666'
                  }}>
                    {deal.features.slice(0, 2).map((feature, index) => (
                      <li key={index} style={{ padding: '2px 0' }}>
                        <i className="fas fa-check" style={{ color: '#28a745', marginRight: '8px' }}></i>
                        {feature}
                      </li>
                    ))}
                    {deal.features.length > 2 && (
                      <li style={{ padding: '2px 0', fontStyle: 'italic' }}>
                        +{deal.features.length - 2} more features
                      </li>
                    )}
                  </ul>
                )}
                
                <div className="deal-price">{deal.price}</div>
                
                <div style={{ display: 'flex', gap: '10px' }}>
                  <button 
                    className="deal-btn"
                    onClick={() => handleBookDeal(deal.title)}
                    style={{ flex: 1 }}
                  >
                    Book Now
                  </button>
                  <button 
                    className="deal-btn"
                    onClick={() => handleDealClick(deal)}
                    style={{ 
                      background: 'transparent',
                      color: '#dc3545',
                      border: '2px solid #dc3545',
                      flex: '0 0 auto',
                      padding: '12px 15px'
                    }}
                  >
                    <i className="fas fa-info-circle"></i>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Deal Details Modal */}
        {showModal && selectedDeal && (
          <div className="modal-overlay" onClick={closeModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <div className="modal-header">
                <h3>{selectedDeal.title}</h3>
                <button className="modal-close" onClick={closeModal}>
                  <i className="fas fa-times"></i>
                </button>
              </div>
              
              <div className="modal-body">
                <div style={{ textAlign: 'center', marginBottom: '20px' }}>
                  <span style={{ fontSize: '4rem' }}>
                    {getDealIcon(selectedDeal.category)}
                  </span>
                </div>
                
                <p style={{ fontSize: '1.1rem', marginBottom: '20px' }}>
                  {selectedDeal.description}
                </p>
                
                <div style={{ 
                  background: '#f8f9fa', 
                  padding: '15px', 
                  borderRadius: '8px',
                  marginBottom: '20px'
                }}>
                  <h4 style={{ color: '#2c3e95', marginBottom: '10px' }}>
                    What's Included:
                  </h4>
                  {selectedDeal.features && (
                    <ul style={{ listStyle: 'none', padding: '0' }}>
                      {selectedDeal.features.map((feature, index) => (
                        <li key={index} style={{ padding: '5px 0', color: '#555' }}>
                          <i className="fas fa-check" style={{ color: '#28a745', marginRight: '10px' }}></i>
                          {feature}
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
                
                <div style={{ 
                  textAlign: 'center',
                  padding: '20px',
                  background: 'linear-gradient(45deg, #dc3545, #c82333)',
                  color: 'white',
                  borderRadius: '8px',
                  marginBottom: '20px'
                }}>
                  <h3 style={{ margin: '0 0 10px 0' }}>{selectedDeal.price}</h3>
                  {selectedDeal.validTill && (
                    <p style={{ margin: '0', opacity: '0.9' }}>
                      Offer valid till {selectedDeal.validTill}
                    </p>
                  )}
                </div>
                
                <div style={{ textAlign: 'center' }}>
                  <button 
                    className="cta-button"
                    onClick={() => handleBookDeal(selectedDeal.title)}
                    style={{ marginRight: '10px', minWidth: '120px' }}
                  >
                    Book Now
                  </button>
                  <button 
                    className="cta-button secondary"
                    onClick={() => alert('Get more info functionality')}
                    style={{ minWidth: '120px' }}
                  >
                    Get Info
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Newsletter Signup */}
        <div style={{
          marginTop: '60px',
          textAlign: 'center',
          background: 'linear-gradient(135deg, #f8f9fa, #e9ecef)',
          padding: '40px',
          borderRadius: '15px'
        }}>
          <h3 style={{ color: '#2c3e95', marginBottom: '15px' }}>
            Never Miss a Deal!
          </h3>
          <p style={{ color: '#666', marginBottom: '30px' }}>
            Subscribe to our newsletter and get exclusive offers delivered to your inbox.
          </p>
          <div style={{ 
            display: 'flex', 
            maxWidth: '400px', 
            margin: '0 auto',
            gap: '10px'
          }}>
            <input 
              type="email" 
              placeholder="Enter your email"
              style={{
                flex: 1,
                padding: '12px',
                border: '2px solid #dee2e6',
                borderRadius: '25px',
                outline: 'none'
              }}
            />
            <button 
              className="cta-button"
              onClick={() => alert('Newsletter subscription functionality')}
            >
              Subscribe
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default DealsOffers;
