import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import Services from './components/Services';
import Stats from './components/Stats';
import DealsOffers from './components/DealsOffers';
import CaseStudies from './components/CaseStudies';
import Footer from './components/Footer';

// API Configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function App() {
  const [loading, setLoading] = useState(true);
  const [services, setServices] = useState([]);
  const [deals, setDeals] = useState([]);
  const [stats, setStats] = useState({});
  const [caseStudies, setCaseStudies] = useState([]);
  const [error, setError] = useState(null);

  // Helper function to make API calls
  const fetchData = async (endpoint, fallbackData = []) => {
    try {
      const response = await fetch(`${API_BASE_URL}/${endpoint}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const result = await response.json();
      return result.success ? result.data : fallbackData;
    } catch (error) {
      console.warn(`Failed to fetch ${endpoint}:`, error);
      return fallbackData;
    }
  };

  useEffect(() => {
    const loadAllData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch all data concurrently
        const [servicesData, dealsData, statsData, caseStudiesData] = await Promise.all([
          fetchData('services', getDefaultServices()),
          fetchData('deals', getDefaultDeals()),
          fetchData('stats', getDefaultStats()),
          fetchData('case-studies', getDefaultCaseStudies())
        ]);

        setServices(servicesData);
        setDeals(dealsData);
        setStats(statsData);
        setCaseStudies(caseStudiesData);

      } catch (error) {
        console.error('Error loading data:', error);
        setError('Failed to load some content. Using default data.');
        
        // Set fallback data
        setServices(getDefaultServices());
        setDeals(getDefaultDeals());
        setStats(getDefaultStats());
        setCaseStudies(getDefaultCaseStudies());
      } finally {
        setLoading(false);
      }
    };

    loadAllData();
  }, []);

  // Default data functions
  const getDefaultServices = () => [
    {
      id: 1,
      name: "Newspaper Advertising",
      description: "Book your Newspaper display ads in any regional or National Newspaper, Instantly!",
      icon: "📰",
      slug: "newspaper-advertising",
      features: ["Display Ads", "Classified Ads", "Obituary", "Tender Notice", "Matrimonial", "Property Ads"],
      benefits: ["Instant booking", "Lowest prices", "Wide network", "Professional design"]
    },
    {
      id: 2,
      name: "Digital Marketing",
      description: "Complete digital marketing solutions to boost your online presence",
      icon: "💻",
      slug: "digital-marketing",
      features: ["Social Media Marketing", "SEO", "PPC", "Content Marketing", "Email Marketing"],
      benefits: ["Targeted reach", "Measurable results", "Cost-effective", "24/7 presence"]
    },
    {
      id: 3,
      name: "Radio Advertising",
      description: "Reach millions through strategic radio campaigns",
      icon: "📻",
      slug: "radio-advertising",
      features: ["Prime Time Slots", "RJ Mentions", "Sponsorship", "Custom Jingles"],
      benefits: ["High frequency", "Local penetration", "Audio branding", "Cost-effective"]
    },
    {
      id: 4,
      name: "Cinema Advertising",
      description: "Advertise in cinema halls nationwide with maximum impact",
      icon: "🎬",
      slug: "cinema-advertising",
      features: ["On-Screen Ads", "Lobby Display", "Slide Ads", "Standee Placement"],
      benefits: ["Captive audience", "High impact", "Premium demographics", "Enhanced recall"]
    }
  ];

  const getDefaultDeals = () => [
    {
      id: 1,
      title: "Hire Dedicated Media Planner",
      subtitle: "for your business Today!",
      price: "@ just 499/-*",
      originalPrice: "2000/-",
      discount: "75% OFF",
      image: "/api/placeholder/400/250",
      category: "media-planning",
      features: ["Personalized strategy", "Market analysis", "Campaign optimization"],
      cta: "Hire Now",
      popular: true
    },
    {
      id: 2,
      title: "Let the whole city",
      subtitle: "hear about your brand",
      price: "Go on air only at Rs. 9999*",
      originalPrice: "25000/-",
      discount: "60% OFF",
      image: "/api/placeholder/400/250",
      category: "radio",
      features: ["Prime time slots", "Professional recording", "Multiple stations"],
      cta: "Book Now",
      popular: false
    }
  ];

  const getDefaultStats = () => ({
    customers: "50000+",
    teamMembers: "200+",
    advertisingChoices: "1000+",
    vendorCollaborations: "5000+",
    campaigns: "25000+",
    specializedAreas: "15+",
    yearsOfExperience: "10+",
    citiesCovered: "500+"
  });

  const getDefaultCaseStudies = () => [
    {
      id: 1,
      title: "CHEP India - Sustainability Campaign",
      description: "Comprehensive awareness campaign about sustainable practices and unauthorized equipment usage",
      industry: "Logistics",
      client: "CHEP India",
      results: {
        brandAwareness: "40% increase",
        reach: "2.5 million",
        engagement: "15% CTR"
      },
      image: "/api/placeholder/300/200"
    },
    {
      id: 2,
      title: "Binomo - Pan India Download Campaign",
      description: "Multi-channel brand awareness and app download campaign targeting tier-1 and tier-2 cities",
      industry: "Fintech",
      client: "Binomo",
      results: {
        appDownloads: "300% increase",
        reach: "5 million",
        costPerInstall: "Reduced by 45%"
      },
      image: "/api/placeholder/300/200"
    }
  ];

  // Loading component
  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#f5f5f5',
        fontFamily: 'Arial, sans-serif'
      }}>
        <div style={{
          textAlign: 'center',
          padding: '20px'
        }}>
          <div style={{
            width: '50px',
            height: '50px',
            border: '4px solid #4472C4',
            borderTop: '4px solid transparent',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 20px'
          }}></div>
          <div style={{
            fontSize: '18px',
            color: '#4472C4',
            fontWeight: 'bold'
          }}>
            Loading BookMyAd...
          </div>
          <style>
            {`
              @keyframes spin {
                0% { transform: rotate(0deg); }
                100% { transform: rotate(360deg); }
              }
            `}
          </style>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      {/* Error notification */}
      {error && (
        <div style={{
          backgroundColor: '#fff3cd',
          color: '#856404',
          padding: '10px',
          textAlign: 'center',
          borderBottom: '1px solid #ffeaa7',
          fontSize: '14px'
        }}>
          ⚠️ {error}
        </div>
      )}

      {/* Main content */}
      <Header />
      <main>
        <Hero />
        <Services services={services} />
        <Stats stats={stats} />
        <DealsOffers deals={deals} />
        <CaseStudies caseStudies={caseStudies} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
