import React, { useState, useEffect } from "react";

const Hero = () => {
  const slides = [
    {
      title: "Winter Offer",
      subtitle: "Use code WINTER50 — Get article published in just 48 hours!",
      logos: [
        "zee-news.png",
        "mid-day.png",
        "oneindia.png",
        "times-now.png",
        "ani.png",
        "abp.png",
        "aaj-tak.png",
        "filmibeat.png",
      ],
      button: "BOOK NOW!",
    },
    {
      title: "Special Promotion",
      subtitle: "Flat 30% off on all bookings this week only!",
      logos: [
        "zee-news.png",
        "mid-day.png",
        "times-now.png",
        "abp.png",
        "filmibeat.png",
      ],
      button: "BOOK NOW!",
    },
  ];

  const [current, setCurrent] = useState(0);

  // Auto-slide every 4 seconds
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % slides.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  const goPrev = () => setCurrent((prev) => (prev - 1 + slides.length) % slides.length);
  const goNext = () => setCurrent((prev) => (prev + 1) % slides.length);

  return (
    <div style={{ position: "relative", overflow: "hidden", fontFamily: "Arial, sans-serif" }}>
      {/* Slide */}
      {slides.map((slide, index) => (
        <div
          key={index}
          style={{
            width: "100%",
            display: index === current ? "flex" : "none",
            justifyContent: "center",
            alignItems: "center",
            background: "#cfe7ff",
            padding: "30px 0",
            transition: "opacity 0.8s ease-in-out",
          }}
        >
          <div
            style={{
              maxWidth: "1000px",
              width: "100%",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              textAlign: "center",
              background: "white",
              borderRadius: "10px",
              padding: "20px",
              boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
            }}
          >
            <div style={{ fontSize: "28px", fontWeight: "bold", color: "#0a2a66" }}>
              {slide.title}
            </div>
            <div style={{ margin: "10px 0", fontSize: "14px", color: "#333" }}>
              {slide.subtitle}
            </div>

            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                gap: "15px",
                justifyContent: "center",
                margin: "15px 0",
              }}
            >
              {slide.logos.map((logo, i) => (
                <img key={i} src={logo} alt="Logo" style={{ height: "40px" }} />
              ))}
            </div>

            <div style={{ fontSize: "16px", color: "#444" }}>
              Don't miss the opportunity to share your story.
            </div>
            <button
              style={{
                marginTop: "15px",
                backgroundColor: "red",
                color: "white",
                padding: "10px 20px",
                fontSize: "16px",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
              }}
            >
              {slide.button}
            </button>
          </div>
        </div>
      ))}

      {/* Nav Arrows */}
      <div
        onClick={goPrev}
        style={{
          position: "absolute",
          left: "10px",
          top: "50%",
          transform: "translateY(-50%)",
          background: "white",
          borderRadius: "50%",
          padding: "8px",
          cursor: "pointer",
          userSelect: "none",
        }}
      >
        &#10094;
      </div>
      <div
        onClick={goNext}
        style={{
          position: "absolute",
          right: "10px",
          top: "50%",
          transform: "translateY(-50%)",
          background: "white",
          borderRadius: "50%",
          padding: "8px",
          cursor: "pointer",
          userSelect: "none",
        }}
      >
        &#10095;
      </div>
    </div>
  );
};

export default Hero;
