import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, ChevronDown } from 'lucide-react';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isServicesDropdownOpen, setIsServicesDropdownOpen] = useState(false);
  const location = useLocation();

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      setIsScrolled(scrollTop > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu when route changes
  useEffect(() => {
    setIsMobileMenuOpen(false);
    setIsServicesDropdownOpen(false);
  }, [location]);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleServicesDropdown = () => {
    setIsServicesDropdownOpen(!isServicesDropdownOpen);
  };

  const services = [
    { name: 'Newspaper Ads', path: '/services#newspaper' },
    { name: 'Digital Marketing', path: '/services#digital' },
    { name: 'Radio Advertising', path: '/services#radio' },
    { name: 'TV Advertising', path: '/services#tv' },
    { name: 'Cinema Ads', path: '/services#cinema' },
    { name: 'Outdoor Advertising', path: '/services#outdoor' }
  ];

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        <div className="header-content">
          {/* Logo */}
          <Link to="/" className="logo" aria-label="BookMyAd Home">
            BookMyAd
          </Link>

          {/* Desktop Navigation */}
          <nav className="nav" role="navigation" aria-label="Main Navigation">
            <Link 
              to="/" 
              className={location.pathname === '/' ? 'active' : ''}
            >
              Home
            </Link>
            
            {/* Services Dropdown */}
            <div className="nav-dropdown" onMouseEnter={() => setIsServicesDropdownOpen(true)} onMouseLeave={() => setIsServicesDropdownOpen(false)}>
              <Link 
                to="/services" 
                className={`nav-dropdown-toggle ${location.pathname === '/services' ? 'active' : ''}`}
                onClick={(e) => {
                  e.preventDefault();
                  toggleServicesDropdown();
                }}
              >
                Services <ChevronDown size={16} />
              </Link>
              
              {isServicesDropdownOpen && (
                <div className="dropdown-menu">
                  {services.map((service, index) => (
                    <Link 
                      key={index}
                      to={service.path} 
                      className="dropdown-item"
                      onClick={() => setIsServicesDropdownOpen(false)}
                    >
                      {service.name}
                    </Link>
                  ))}
                </div>
              )}
            </div>

            <Link 
              to="/case-studies" 
              className={location.pathname === '/case-studies' ? 'active' : ''}
            >
              Case Studies
            </Link>
            
            <Link 
              to="/about" 
              className={location.pathname === '/about' ? 'active' : ''}
            >
              About
            </Link>
            
            <Link 
              to="/contact" 
              className={location.pathname === '/contact' ? 'active' : ''}
            >
              Contact
            </Link>

            <Link to="/book-now" className="btn btn-primary">
              Book Now
            </Link>
          </nav>

          {/* Mobile Menu Toggle */}
          <button 
            className="mobile-menu-toggle"
            onClick={toggleMobileMenu}
            aria-label="Toggle Mobile Menu"
            aria-expanded={isMobileMenuOpen}
          >
            {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Navigation */}
        {isMobileMenuOpen && (
          <div className="mobile-nav" role="navigation" aria-label="Mobile Navigation">
            <div className="mobile-nav-content">
              <Link 
                to="/" 
                className={`mobile-nav-item ${location.pathname === '/' ? 'active' : ''}`}
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Home
              </Link>
              
              <div className="mobile-nav-dropdown">
                <button 
                  className="mobile-nav-item mobile-dropdown-toggle"
                  onClick={toggleServicesDropdown}
                  aria-expanded={isServicesDropdownOpen}
                >
                  Services <ChevronDown size={16} />
                </button>
                
                {isServicesDropdownOpen && (
                  <div className="mobile-dropdown-menu">
                    {services.map((service, index) => (
                      <Link 
                        key={index}
                        to={service.path} 
                        className="mobile-dropdown-item"
                        onClick={() => {
                          setIsMobileMenuOpen(false);
                          setIsServicesDropdownOpen(false);
                        }}
                      >
                        {service.name}
                      </Link>
                    ))}
                  </div>
                )}
              </div>

              <Link 
                to="/case-studies" 
                className={`mobile-nav-item ${location.pathname === '/case-studies' ? 'active' : ''}`}
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Case Studies
              </Link>
              
              <Link 
                to="/about" 
                className={`mobile-nav-item ${location.pathname === '/about' ? 'active' : ''}`}
                onClick={() => setIsMobileMenuOpen(false)}
              >
                About
              </Link>
              
              <Link 
                to="/contact" 
                className={`mobile-nav-item ${location.pathname === '/contact' ? 'active' : ''}`}
                onClick={() => setIsMobileMenuOpen(false)}
              >
                Contact
              </Link>

              <div className="mobile-nav-cta">
                <Link 
                  to="/book-now" 
                  className="btn btn-primary"
                  onClick={() => setIsMobileMenuOpen(false)}
                >
                  Book Now
                </Link>
              </div>
            </div>
          </div>
        )}
      </div>

      <style jsx>{`
        .nav-dropdown {
          position: relative;
        }

        .nav-dropdown-toggle {
          display: flex;
          align-items: center;
          gap: 4px;
          cursor: pointer;
        }

        .dropdown-menu {
          position: absolute;
          top: 100%;
          left: 0;
          background: rgba(255, 255, 255, 0.98);
          backdrop-filter: blur(20px);
          border: 1px solid rgba(229, 231, 235, 0.8);
          border-radius: 12px;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1);
          padding: 0.5rem 0;
          min-width: 200px;
          z-index: 1000;
          animation: fadeInDown 0.3s ease;
        }

        .dropdown-item {
          display: block;
          padding: 0.75rem 1rem;
          color: #4b5563;
          text-decoration: none;
          transition: all 0.2s ease;
          border: none;
          background: none;
          width: 100%;
          text-align: left;
        }

        .dropdown-item:hover {
          background: rgba(59, 130, 246, 0.1);
          color: #3b82f6;
        }

        .mobile-nav {
          background: rgba(255, 255, 255, 0.98);
          backdrop-filter: blur(20px);
          border-top: 1px solid rgba(229, 231, 235, 0.8);
          padding: 1rem 0;
          animation: slideDown 0.3s ease;
        }

        .mobile-nav-content {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .mobile-nav-item {
          padding: 0.75rem 1rem;
          color: #4b5563;
          text-decoration: none;
          border-radius: 8px;
          transition: all 0.2s ease;
          display: flex;
          align-items: center;
          justify-content: space-between;
          background: none;
          border: none;
          font-size: 1rem;
          cursor: pointer;
          width: 100%;
          text-align: left;
        }

        .mobile-nav-item:hover,
        .mobile-nav-item.active {
          background: rgba(59, 130, 246, 0.1);
          color: #3b82f6;
        }

        .mobile-dropdown-menu {
          padding-left: 1rem;
          border-left: 2px solid rgba(59, 130, 246, 0.2);
          margin-left: 1rem;
          margin-top: 0.5rem;
        }

        .mobile-dropdown-item {
          padding: 0.5rem 1rem;
          color: #6b7280;
          text-decoration: none;
          display: block;
          border-radius: 6px;
          transition: all 0.2s ease;
          font-size: 0.9rem;
        }

        .mobile-dropdown-item:hover {
          background: rgba(59, 130, 246, 0.1);
          color: #3b82f6;
        }

        .mobile-nav-cta {
          margin-top: 1rem;
          padding-top: 1rem;
          border-top: 1px solid rgba(229, 231, 235, 0.8);
        }

        @keyframes fadeInDown {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes slideDown {
          from {
            opacity: 0;
            transform: translateY(-20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @media (max-width: 768px) {
          .nav {
            display: none;
          }
          
          .mobile-menu-toggle {
            display: flex;
          }
        }

        @media (min-width: 769px) {
          .mobile-menu-toggle {
            display: none;
          }
          
          .mobile-nav {
            display: none;
          }
        }
      `}</style>
    </header>
  );
};

export default Header;
