import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ArrowRight, Play, Star, Users, Award, Zap } from 'lucide-react';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isVisible, setIsVisible] = useState(false);

  // Animation trigger
  useEffect(() => {
    setIsVisible(true);
  }, []);

  // Auto-rotating hero content
  const heroSlides = [
    {
      title: "360Â° Digital Marketing Solutions",
      subtitle: "BookMyAd - Your Gateway to Global Advertising Success",
      description: "From newspaper ads to digital campaigns, we provide comprehensive advertising solutions that help businesses reach their target audience effectively.",
      cta: "Start Your Campaign",
      features: ["Pan India Coverage", "Expert Team", "Proven Results"]
    },
    {
      title: "Simplify Your Ad Booking Process",
      subtitle: "Quick, Easy & Stress-Free Advertising",
      description: "Book ads in newspapers, digital platforms, radio, TV, and outdoor media - all from one convenient platform. No hassle, just results.",
      cta: "Book Now",
      features: ["One-Click Booking", "Multiple Platforms", "24/7 Support"]
    },
    {
      title: "Trusted by 50,000+ Satisfied Customers",
      subtitle: "Leading Ad Aggregator Platform",
      description: "Join thousands of businesses who trust BookMyAd for their advertising needs. From startups to Fortune 500 companies.",
      cta: "Join Us Today",
      features: ["50K+ Customers", "1000+ Vendors", "5000+ Campaigns"]
    }
  ];

  // Auto-slide functionality
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroSlides.length);
    }, 6000);

    return () => clearInterval(interval);
  }, [heroSlides.length]);

  const currentHero = heroSlides[currentSlide];

  const handleVideoPlay = () => {
    // In a real app, this would open a video modal or redirect to a video
    console.log('Playing demo video...');
    alert('Demo video would play here!');
  };

  return (
    <section className="hero">
      <div className="container">
        <div className={`hero-content ${isVisible ? 'visible' : ''}`}>
          {/* Main Hero Content */}
          <div className="hero-main">
            <div className="hero-badge">
              <Star className="hero-badge-icon" size={16} />
              <span>Rated #1 Ad Booking Platform</span>
            </div>

            <h1 className="hero-title">
              {currentHero.title}
            </h1>

            <p className="hero-subtitle">
              {currentHero.subtitle}
            </p>

            <p className="hero-description">
              {currentHero.description}
            </p>

            {/* Hero Features */}
            <div className="hero-features">
              {currentHero.features.map((feature, index) => (
                <div key={index} className="hero-feature">
                  <Zap className="hero-feature-icon" size={16} />
                  <span>{feature}</span>
                </div>
              ))}
            </div>

            {/* Call to Action Buttons */}
            <div className="hero-cta">
              <Link to="/book-now" className="btn btn-primary btn-lg">
                {currentHero.cta}
                <ArrowRight size={20} />
              </Link>
              
              <button 
                className="btn btn-secondary btn-lg"
                onClick={handleVideoPlay}
                aria-label="Watch Demo Video"
              >
                <Play size={20} />
                Watch Demo
              </button>
            </div>

            {/* Trust Indicators */}
            <div className="trust-indicators">
              <div className="trust-item">
                <Users className="trust-icon" size={24} />
                <div className="trust-text">
