import React, { useState, useEffect } from 'react';
import HeaderHero from './components/HeaderHero';
import Services from './components/Services';
import Stats from './components/Stats';
import DealsOffers from './components/DealsOffers';
import CaseStudies from './components/CaseStudies';
import Footer from './components/Footer';

function App() {
  const [loading, setLoading] = useState(true);
  const [services, setServices] = useState([]);
  const [deals, setDeals] = useState([]);
  const [stats, setStats] = useState({});
  const [caseStudies, setCaseStudies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      await new Promise(resolve => setTimeout(resolve, 1000));

      setServices([
        { id: 1, name: "Newspaper Advertising", description: "Book ads in leading newspapers across India", icon: "ðŸ“°", features: ["Display Ads", "Classified Ads", "Obituary", "Tender Notice"] },
        { id: 2, name: "Digital Marketing", description: "Complete digital marketing solutions", icon: "ðŸ’»", features: ["Social Media Marketing", "SEO", "PPC", "Content Marketing"] },
        { id: 3, name: "Radio Advertising", description: "Reach millions through radio campaigns", icon: "ðŸ“»", features: ["Prime Time Slots", "RJ Mentions", "Sponsorship", "Jingles"] },
        { id: 4, name: "Cinema Advertising", description: "Advertise in cinema halls nationwide", icon: "ðŸŽ¬", features: ["On-Screen Ads", "Lobby Display", "Slide Ads", "Video Ads"] }
      ]);

      setDeals([
        { id: 1, title: "Lights, Camera, Advertise!", description: "Reach Millions of Eyeballs with Hyperlocal Cinema Ad!", price: "Starting at Rs. 11000/-*", category: "cinema" },
        { id: 2, title: "Hire Dedicated Media Planner", description: "for your business Today!", price: "@ just 499/-*", category: "media-planning" }
      ]);

      setStats({
        customers: "50000+",
        teamMembers: "200+",
        advertisingChoices: "1000+",
        vendorCollaborations: "5000+",
        campaigns: "25000+",
        specializedAreas: "15+"
      });

      setCaseStudies([
        { id: 1, title: "CHEP India - Sustainability Campaign", description: "Raised awareness about sustainable practices and unauthorized equipment usage", industry: "Logistics", results: "40% increase in brand awareness" },
        { id: 2, title: "Binomo - Pan India Download Campaign", description: "Created brand awareness and boosted app downloads across India", industry: "Fintech", results: "300% increase in app downloads" }
      ]);

      setLoading(false);
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="App">
      <HeaderHero />
      <Services services={services} />
      <Stats stats={stats} />
      <DealsOffers deals={deals} />
      <CaseStudies caseStudies={caseStudies} />
      <Footer />
    </div>
  );
}

export default App;
