import React from 'react';

const Hero = () => {
  const handleBookNow = () => {
    alert('Booking functionality would be implemented here');
  };

  return (
    <section style={styles.hero}>
      <div style={styles.heroContent}>
        <div style={styles.heroText}>
          <h1 style={styles.heroTitle}>
            Easiest and Fastest ad booking<br />
            <span style={styles.heroSubtitle}>at the Lowest Price</span>
          </h1>
          <p style={styles.heroDescription}>
            Book your Newspaper display ads in any regional or National Newspaper, Instantly!
          </p>
          <div style={styles.buttonContainer}>
            <button 
              style={styles.heroButton}
              onClick={handleBookNow}
            >
              BOOK NOW
            </button>
            <button style={styles.secondaryButton}>
              HOW IT WORKS
            </button>
          </div>
        </div>
        <div style={styles.heroImage}>
          <img 
            src="/hero-image.png" 
            alt="Advertising" 
            style={styles.image}
          />
        </div>
      </div>
    </section>
  );
};

const styles = {
  hero: {
    backgroundColor: '#f8f9fa',
    padding: '100px 20px 80px',
    position: 'relative',
    overflow: 'hidden',
  },
  heroContent: {
    display: 'flex',
    alignItems: 'center',
    maxWidth: '1200px',
    margin: '0 auto',
    gap: '50px',
  },
  heroText: {
    flex: 1,
    textAlign: 'left',
    zIndex: 2,
  },
  heroTitle: {
    fontSize: '3rem',
    marginBottom: '20px',
    lineHeight: '1.2',
    fontWeight: '700',
    color: '#1a1a1a',
  },
  heroSubtitle: {
    color: '#ff6b00',
    display: 'inline-block',
    background: 'linear-gradient(90deg, #FF6B00 0%, #FF8C00 100%)',
    WebkitBackgroundClip: 'text',
    WebkitTextFillColor: 'transparent',
  },
  heroDescription: {
    fontSize: '1.25rem',
    marginBottom: '30px',
    lineHeight: '1.6',
    color: '#4a4a4a',
    maxWidth: '500px',
  },
  buttonContainer: {
    display: 'flex',
    gap: '20px',
    marginTop: '30px',
  },
  heroButton: {
    backgroundColor: '#ff6b00',
    color: '#fff',
    border: 'none',
    padding: '15px 35px',
    fontSize: '1rem',
    borderRadius: '8px',
    cursor: 'pointer',
    fontWeight: '600',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 15px rgba(255, 107, 0, 0.3)',
    '&:hover': {
      transform: 'translateY(-2px)',
      boxShadow: '0 6px 20px rgba(255, 107, 0, 0.4)',
    }
  },
  secondaryButton: {
    backgroundColor: 'transparent',
    color: '#1a1a1a',
    border: '2px solid #1a1a1a',
    padding: '15px 35px',
    fontSize: '1rem',
    borderRadius: '8px',
    cursor: 'pointer',
    fontWeight: '600',
    transition: 'all 0.3s ease',
    '&:hover': {
      backgroundColor: '#f0f0f0',
    }
  },
  heroImage: {
    flex: 1,
    position: 'relative',
    zIndex: 1,
  },
  image: {
    width: '100%',
    maxWidth: '600px',
    height: 'auto',
    borderRadius: '10px',
    boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
  },
  // Media queries
  '@media (max-width: 992px)': {
    heroContent: {
      flexDirection: 'column',
      textAlign: 'center',
    },
    heroText: {
      textAlign: 'center',
    },
    heroDescription: {
      margin: '0 auto 30px',
    },
    buttonContainer: {
      justifyContent: 'center',
    },
    heroTitle: {
      fontSize: '2.5rem',
    },
  },
  '@media (max-width: 768px)': {
    hero: {
      padding: '80px 20px 60px',
    },
    heroTitle: {
      fontSize: '2rem',
    },
    heroDescription: {
      fontSize: '1.1rem',
    },
    buttonContainer: {
      flexDirection: 'column',
      gap: '15px',
    },
    heroButton: {
      width: '100%',
    },
    secondaryButton: {
      width: '100%',
    },
  },
  '@media (max-width: 480px)': {
    heroTitle: {
      fontSize: '1.8rem',
    },
  },
};

export default Hero;
