import React from 'react';

const DealsOffers = () => {
  const deals = [
    {
      id: 1,
      title: "Lights, Camera, Advertise!",
      description: "Reach Millions of Eyeballs With Hyperlocal Cinema Ad Starting at Rs. 11000/-!",
      buttonText: "Close Now"
    },
    {
      id: 2,
      title: "Hire Dedicated Media Planner for your business Today!",
      description: "Just â‚¹39/-!",
      buttonText: "Hire Now",
      link: "https://www.hirebook.com/"
    }
  ];

  return (
    <section style={styles.dealsSection}>
      <div style={styles.container}>
        <h2 style={styles.sectionTitle}>Deals and Offers</h2>
        
        <div style={styles.dealsContainer}>
          {deals.map(deal => (
            <div key={deal.id} style={styles.dealCard}>
              <h3 style={styles.dealTitle}>{deal.title}</h3>
              <p style={styles.dealDescription}>{deal.description}</p>
              {deal.link ? (
                <a 
                  href={deal.link} 
                  style={styles.dealButton}
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  {deal.buttonText}
                </a>
              ) : (
                <button style={styles.dealButton}>
                  {deal.buttonText}
                </button>
              )}
            </div>
          ))}
        </div>
        
        <div style={styles.chatExpert}>
          <p style={styles.chatText}>Online Chat with expert</p>
        </div>
      </div>
    </section>
  );
};

const styles = {
  dealsSection: {
    padding: '60px 20px',
    backgroundColor: '#fff',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    textAlign: 'center',
  },
  sectionTitle: {
    fontSize: '2rem',
    marginBottom: '20px',
    color: '#333',
  },
  dealsContainer: {
    display: 'flex',
    justifyContent: 'center',
    gap: '30px',
    marginTop: '30px',
    flexWrap: 'wrap',
  },
  dealCard: {
    backgroundColor: '#fff',
    padding: '30px',
    borderRadius: '10px',
    boxShadow: '0 5px 15px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '400px',
    textAlign: 'center',
  },
  dealTitle: {
    fontSize: '1.5rem',
    marginBottom: '15px',
    color: '#333',
  },
  dealDescription: {
    fontSize: '1.1rem',
    marginBottom: '25px',
    color: '#666',
    lineHeight: '1.6',
  },
  dealButton: {
    display: 'inline-block',
    padding: '12px 30px',
    backgroundColor: '#ff6b00',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    fontSize: '1rem',
    cursor: 'pointer',
    textDecoration: 'none',
    transition: 'background-color 0.3s',
    fontWeight: '600',
    '&:hover': {
      backgroundColor: '#e05d00'
    }
  },
  chatExpert: {
    marginTop: '40px',
    fontStyle: 'italic',
    color: '#666',
    fontSize: '1.1rem',
  },
  chatText: {
    position: 'relative',
    display: 'inline-block',
    paddingLeft: '30px',
    '&::before': {
      content: '"ðŸ’¬"',
      position: 'absolute',
      left: '0',
      top: '50%',
      transform: 'translateY(-50%)'
    }
  }
};

export default DealsOffers;
