import React, { useState, useEffect } from 'react';
import heroImage1 from './hero1.jpg'; // Replace with your image paths
import heroImage2 from './hero2.jpg';
import heroImage3 from './hero3.jpg';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  
  const slides = [
    {
      title: "Hire Dedicated Media Planner",
      subtitle: "For your business Today!",
      description: "Just £99/-",
      buttonText: "Learn More",
      image: heroImage1
    },
    {
      title: "Let the whole city hear about your brand!",
      subtitle: "USE CODE | WINTER50",
      description: "Book Now",
      buttonText: "View Offers",
      image: heroImage2
    },
    {
      title: "Get Published in Top Media",
      subtitle: "Digital PR Packages",
      description: "Starting from £199",
      buttonText: "Explore",
      image: heroImage3
    }
  ];

  // Auto-rotate slides every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  return (
    <div style={styles.container}>
      {/* Navigation Links */}
      <div style={styles.navLinks}>
        <a href="#home" style={styles.navLink}>Home</a>
        <a href="#newspaper" style={styles.navLink}>Newspaper</a>
        <a href="#digital" style={styles.navLink}>Digital</a>
        <a href="#radio" style={styles.navLink}>Radio</a>
        <a href="#cinema" style={styles.navLink}>Cinema</a>
        <a href="#blog" style={styles.navLink}>Blog</a>
        <a href="#login" style={styles.navLink}>Login</a>
        <a href="#contact" style={styles.navLink}>Contact Us</a>
      </div>

      {/* Hero Image Carousel */}
      <div style={styles.heroContainer}>
        <div style={styles.slidesContainer}>
          {slides.map((slide, index) => (
            <div 
              key={index}
              style={{
                ...styles.slide,
                opacity: index === currentSlide ? 1 : 0,
                zIndex: index === currentSlide ? 1 : 0
              }}
            >
              <img 
                src={slide.image} 
                alt={slide.title} 
                style={styles.heroImage}
              />
              <div style={styles.slideContent}>
                <h2 style={styles.slideTitle}>{slide.title}</h2>
                <h3 style={styles.slideSubtitle}>{slide.subtitle}</h3>
                <p style={styles.slideDescription}>{slide.description}</p>
                <button style={styles.slideButton}>
                  {slide.buttonText}
                </button>
              </div>
            </div>
          ))}
        </div>
        
        {/* Navigation Arrows */}
        <button style={styles.prevButton} onClick={prevSlide}>‹</button>
        <button style={styles.nextButton} onClick={nextSlide}>›</button>
        
        {/* Slide Indicators */}
        <div style={styles.indicators}>
          {slides.map((_, index) => (
            <button
              key={index}
              style={{
                ...styles.indicator,
                backgroundColor: index === currentSlide ? '#ff6b00' : '#ccc'
              }}
              onClick={() => setCurrentSlide(index)}
            />
          ))}
        </div>
      </div>

      {/* Deals Section */}
      <div style={styles.dealsContainer}>
        <h3 style={styles.dealsTitle}>Deals and Offers</h3>
        <div style={styles.dealsGrid}>
          <div style={styles.dealCard}>
            <p style={styles.dealText}>Let the whole city hear about your brand!</p>
            <p style={styles.codeText}>USE CODE | WINTER50</p>
            <button style={styles.dealButton}>Book Now</button>
            <div style={styles.newsOutlets}>
              <span style={styles.outlet}>ZEENEWS</span>
              <span style={styles.outlet}>mid-day</span>
              <span style={styles.outlet}>oneindia</span>
            </div>
          </div>
          <div style={styles.dealCard}>
            <p style={styles.dealText}>Hire Dedicated Media Planner</p>
            <p style={styles.priceText}>Just £99/-</p>
            <button style={styles.dealButton}>Learn More</button>
          </div>
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Arial', sans-serif",
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
  },
  navLinks: {
    display: 'flex',
    justifyContent: 'center',
    gap: '25px',
    marginBottom: '20px',
    flexWrap: 'wrap',
    padding: '10px 0',
    borderBottom: '1px solid #eee',
  },
  navLink: {
    color: '#333',
    textDecoration: 'none',
    fontWeight: '600',
    fontSize: '16px',
    padding: '5px 0',
    transition: 'color 0.3s',
    '&:hover': {
      color: '#ff6b00',
    }
  },
  heroContainer: {
    position: 'relative',
    height: '500px',
    marginBottom: '40px',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 5px 15px rgba(0,0,0,0.1)',
  },
  slidesContainer: {
    position: 'relative',
    width: '100%',
    height: '100%',
  },
  slide: {
    position: 'absolute',
    width: '100%',
    height: '100%',
    transition: 'opacity 0.5s ease',
  },
  heroImage: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  slideContent: {
    position: 'absolute',
    bottom: '50px',
    left: '50px',
    maxWidth: '500px',
    backgroundColor: 'rgba(255,255,255,0.9)',
    padding: '30px',
    borderRadius: '10px',
    boxShadow: '0 5px 15px rgba(0,0,0,0.1)',
  },
  slideTitle: {
    fontSize: '28px',
    fontWeight: '700',
    marginBottom: '10px',
    color: '#333',
  },
  slideSubtitle: {
    fontSize: '20px',
    fontWeight: '600',
    marginBottom: '15px',
    color: '#ff6b00',
  },
  slideDescription: {
    fontSize: '16px',
    marginBottom: '20px',
    color: '#555',
  },
  slideButton: {
    backgroundColor: '#ff6b00',
    color: 'white',
    border: 'none',
    padding: '12px 30px',
    borderRadius: '5px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
    '&:hover': {
      backgroundColor: '#e05d00',
    }
  },
  prevButton: {
    position: 'absolute',
    left: '20px',
    top: '50%',
    transform: 'translateY(-50%)',
    backgroundColor: 'rgba(0,0,0,0.5)',
    color: 'white',
    border: 'none',
    width: '50px',
    height: '50px',
    borderRadius: '50%',
